const React = require('react');

const defaultValue = {
    title: '',
    priority: 'HIGH',
    status: 'NEW',
    description: '',
}

const TodoContext = React.createContext(defaultValue);
const { Provider, Consumer } = TodoContext;

module.exports = TodoContext;
exports = module.exports;
exports.StaticPropsProvider = Provider;
exports.StaticPropsConsumer = Consumer;
