import React, { useState } from 'react'
import { func } from 'prop-types'

import { STATUS, PRIORITY } from '../../utils/constants'

const namespace = 'formtodo'

const FormTodo = ({ handleAddItem }) => {
  const [title, setTitle] = useState('')
  const [priority, setPriority] = useState('HIGH')
  const [status, setStatus] = useState('NEW')
  const [description, setDescription] = useState('')

  const handleSubmit = e => {
    e.preventDefault()
    handleAddItem({
      id: (+new Date()).toString(),
      title,
      priority,
      status,
      description
    })
    resetForm()
  }

  const resetForm = () => {
    setTitle('')
    setPriority('HIGH')
    setStatus('NEW')
    setDescription('')
  }

  const priorityOptions = Object.keys(PRIORITY).map(key =>
        <option key={key} value={key}>{PRIORITY[key]}</option>
  )

  const statusOptions = Object.keys(STATUS).map(key =>
        <option key={key} value={key}>{STATUS[key]}</option>
  )

  return (
        <div className={`${namespace}`}>
            <form onSubmit={handleSubmit}>
                <div className={`${namespace}__input--text`}>
                    <input
                        type="text"
                        value={title}
                        placeholder={'Titulo'}
                        onChange={e => setTitle(e.target.value)}
                    />
                    <select onChange={e => setPriority(e.target.value)}>
                        { priorityOptions }
                    </select>
                    <select onChange={e => setStatus(e.target.value)}>
                        { statusOptions }
                    </select>
                </div>
                <div className={`${namespace}__input--textarea`}>
                    <textarea id="noter-text-area" name="textarea" value={description} onChange={e => setDescription(e.target.value)} />
                </div>
                <button
                    className={`${namespace}__input--submit`}
                    disabled={title ? '' : 'disabled'}
                >
                    Crear tarea
                </button>
            </form>
        </div>
  )
}

FormTodo.propTypes = {
  handleAddItem: func.isRequired
}

export default FormTodo
