import React, { useState } from "react";

import FormInput from '../FormInput';
import { PRIORITY } from '../../utils/constants';

const namespace = 'formtodo';

const FormTodo = ({ handleAddItem }) => {
    const [title, setTitle] = useState("");
    const [priority, setPriority] = useState("");
    const [status, setStatus] = useState("");
    const [description, setDescription] = useState("");

    const handleSubmit = e => {
        e.preventDefault();
        handleAddItem ({
        id: (+new Date()).toString(),
        title,
        priority,
        status,
        description
        });
        resetForm();
    };

    const resetForm = () => {
        setTitle("");
        setPriority("");
        setStatus("");
        setDescription("");
    }

    const buttonDisabled = (title && priority && status && description);

    const tifOptions = Object.keys(PRIORITY).map(key => 
        <option value={key}>{PRIORITY[key]}</option>
    )

    return (
        <div className={`${namespace}`}>
            <form onSubmit={handleSubmit}>
                <div className={`${namespace}__input--text`}>
                    <FormInput
                        value={title}
                        placeholder={'Titulo'}
                        handleOnChange={e => setTitle(e.target.value)}
                    />
                    <select onChange={e => setPriority(e.target.value)}>
                        { tifOptions }
                    </select>
                    <FormInput
                        value={status}
                        placeholder={'Estado'}
                        handleOnChange={e => setStatus(e.target.value)}
                    />
                </div>
                <div className={`${namespace}__input--textarea`}>
                    <FormInput
                        value={description}
                        placeholder={'Descripcion'}
                        handleOnChange={e => setDescription(e.target.value)}
                    />
                </div>
                <button
                    className={`${namespace}__input--submit`}
                    disabled={buttonDisabled ? "" : "disabled"}
                >
                    Crear tarea
                </button>
            </form>
        </div>
    );
};

export default FormTodo;
