import React from 'react'
import { shape, string } from 'prop-types'

import { STATUS, PRIORITY } from '../../utils/constants'

const namespace = 'taskdata'

const TaskData = ({
  data: {
    title,
    priority,
    status,
    description
  }
}) => {
  const priorityOptions = Object.keys(PRIORITY).map(key => {
    if (priority === key) {
      return <option key={key} value={key} selected>{PRIORITY[key]}</option>
    }
    return <option key={key} value={key}>{PRIORITY[key]}</option>
  }

  )

  const statusOptions = Object.keys(STATUS).map(key =>
      <option key={key} value={key}>{STATUS[key]}</option>
  )

  return (
    <div className={`${namespace}`}>
      <div>
        <p className={`${namespace}__priority`}>Prioridad: {PRIORITY[priority]}</p>
        <select>
          { priorityOptions }
        </select>
        <p className={`${namespace}__status`}>Estado: {STATUS[status]}</p>
        <select>
          { statusOptions }
        </select>
      </div>
      <h2>Titulo: {title}</h2>
      <p>Descripcion<br />{description}</p>
    </div>
  )
}

TaskData.propTypes = {
  data: shape({
    title: string.isRequired,
    priority: string.isRequired,
    status: string.isRequired,
    description: string.isRequired
  }).isRequired
}

export default TaskData
