import React from 'react'
import { shape, string } from 'prop-types'

import { STATUS, PRIORITY } from '../../utils/constants'
import { createDropDown } from '../../utils/utils'

const namespace = 'taskdata'

const TaskData = ({
  data: {
    title,
    priority,
    status,
    description
  }
}) => {
  return (
    <div className={`${namespace}`}>
      <div className={`${namespace}__priority`}>
        <p>Prioridad: {PRIORITY[priority]}</p>
        <select>
          { createDropDown(PRIORITY, priority) }
        </select>
      </div>
      <div className={`${namespace}__status`}>
        <p>Estado: {STATUS[status]}</p>
        <select>
        { createDropDown(STATUS, status) }
        </select>
      </div>
      <h2>Titulo: {title}</h2>
      <p>Descripcion<br />{description}</p>
    </div>
  )
}

TaskData.propTypes = {
  data: shape({
    title: string.isRequired,
    priority: string.isRequired,
    status: string.isRequired,
    description: string.isRequired
  }).isRequired
}

export default TaskData
