import React, { useState } from "react";

import FormInput from '../FormInput';

const namespace = 'formtodo';

const FormTodo = ({ handleAddItem }) => {
    const [title, setTitle] = useState("");
    const [priority, setPriority] = useState("");
    const [status, setStatus] = useState("");
    const [description, setDescription] = useState("");
    const handleSubmit = e => {
    e.preventDefault();
        handleAddItem ({
        id: (+new Date()).toString(),
        title,
        priority,
        status,
        description
        });
        resetForm();
    };

    const resetForm = () => {
        setTitle("");
        setPriority("");
        setStatus("");
        setDescription("");
    }

    const buttonDisabled = (title && priority && status && description);

    return (
    <form onSubmit={handleSubmit}>
        <FormInput value={title} handleOnChange={e => setTitle(e.target.value)} />
        <FormInput value={priority} handleOnChange={e => setPriority(e.target.value)} />
        <FormInput value={status} handleOnChange={e => setStatus(e.target.value)} />
        <FormInput value={description} handleOnChange={e => setDescription(e.target.value)} />
        <button disabled={buttonDisabled ? "" : "disabled"} >Crear tarea</button>
    </form>
    );
};

export default FormTodo;
