import React from 'react'
import { array, func, string } from 'prop-types'

import TaskData from '../TaskData'
import EmptyState from '../EmptyState'

const namespace = 'tasklist'

const TaskList = ({ list, setList, priorityFilter, statusFilter }) => {
  console.log('Prioridad', priorityFilter)
  console.log('Estado', statusFilter)
  const onChangeStatus = e => {
    const { name, value } = e.target
    const updateList = list.map(item => ({
      ...item,
      status: item.id === name ? value : item.done
    }))
    setList(updateList)
  }

  const todoItems2 = list.filter(item => (
    (statusFilter !== 'all' && item.status !== statusFilter) ||
    (priorityFilter !== 'all' && item.priority !== priorityFilter)
  )).map(filteredItem => (
    <TaskData key={filteredItem.id} data={filteredItem} onChange={onChangeStatus} />
  ))

  const todoItems = list.map(item => {
    if (
      (statusFilter !== 'all' && item.status !== statusFilter) ||
      (priorityFilter !== 'all' && item.priority !== priorityFilter)
    ) {
      return null
    } else {
      return (<TaskData key={item.id} data={item} onChange={onChangeStatus} />)
    }
  })

  const renderResult = () => {
    if (list.length > 0) {
      console.log(todoItems)
      if (todoItems2 !== null) {
        return todoItems2
      } else {
        return (<EmptyState text="No se encontro ningun resultado" image="no-results.jpg" />)
      }
    } else {
      return (<EmptyState text="No hay tareas cargadas" image="no-tasks.jpg" />)
    }
  }

  return (
    <div className={`${namespace}`}>
      {renderResult()}
    </div>
  )
}

TaskList.propTypes = {
  list: array.isRequired,
  setList: func.isRequired,
  priorityFilter: string.isRequired,
  statusFilter: string.isRequired
}

export default TaskList
