import React from "react";

import { STATUS, PRIORITY } from '../../utils/constants';

const namespace = 'filters';

const Filters = ({filters, setFilters}) => {
  const priorityOptions = Object.keys(PRIORITY).map(key => 
    <option key={key} value={key}>{PRIORITY[key]}</option>
  )

  const statusOptions = Object.keys(STATUS).map(key => 
      <option key={key} value={key}>{STATUS[key]}</option>
  )

  return (
    <div className={`${namespace}`}>
      Filtros<br /><br />
      Prioridad<br />
      <select onChange={e => setFilters('status', e.target.value)}>
          <option value='all'>Todos</option>
          { priorityOptions }
      </select><br />
      Estado<br />
      <select onChange={e => setFilters('priority', e.target.value)}>
          <option value='all'>Todos</option>
          { statusOptions }
      </select>
    </div>
  );
};

export default Filters;
